const words = require('./words.json')
const { execSync, exec } = require("child_process");
const { writeFileSync } = require('fs')
words.forEach(word => {
    console.log(word)
    writeFileSync('./package.json',`{
        "name": "${word}",
        "version": "0.0.1",
        "description": "",
        "main": "index.js",
        "scripts": {
            "test": "echo \\"Error: no test specified\\" && exit 1"
        },
        "author": "",
        "license": "ISC"
    }`,'utf-8')
    try{
        execSync('npm publish')
    }
    catch(e){

    }
    
})


